scripts.autocannon-rn-no-auth:
yarn autocannon https://graphql.testnet.galoy.io:443/graphql -m POST -H Content-Type=application/json -b '{"query":"query gql_query_anonymous {
  prices {
    __typename
    id
    o
  }
  earnList {
    __typename
    id
    value
  }
  maps {
    __typename
    id
    title
    coordinate {
        __typename
        latitude
        longitude
    }
  }
}","variables":{}}' -c 20 -d 10
